import ButtonLink from "@kiwicom/orbit-components/lib/ButtonLink";
import Card from "@kiwicom/orbit-components/lib/Card";
import CardSection from "@kiwicom/orbit-components/lib/CardSection";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Text from "@kiwicom/orbit-components/lib/Text";

example:
() => {
  const [expanded1, setExpanded1] = React.useState(false)
  const [expanded2, setExpanded2] = React.useState(false)
  return (
    <Card
      title="Passenger info"
      actions={
        <ButtonLink
          size="small"
          onClick={() => {
            const action = expanded1 || expanded2 ? false : true
            setExpanded1(action)
            setExpanded2(action)
          }}
        >
          {expanded1 || expanded2 ? "Close all" : "Expand all"}
        </ButtonLink>
      }
    >
      <CardSection
        expandable
        expanded={expanded1}
        onClose={() => {
          setExpanded1(false)
        }}
        onExpand={() => {
          setExpanded1(true)
        }}
        title={`Yasmin Karenth – ${expanded1 ? "expanded" : "closed"}`}
        icon={<Icons.GenderWoman ariaLabel="female" />}
      >
        <Stack direction="column" spacing="condensed">
          <Text type="secondary">January 20, 1978</Text>
          <Text type="secondary">yas.karenth@example.com</Text>
        </Stack>
      </CardSection>
      <CardSection
        expandable
        expanded={expanded2}
        onClose={() => {
          setExpanded2(false)
        }}
        onExpand={() => {
          setExpanded2(true)
        }}
        title={`Robin Kask – ${expanded2 ? "expanded" : "closed"}`}
        icon={<Icons.GenderMan ariaLabel="male" />}
      >
        <Stack direction="column" spacing="condensed">
          <Text type="secondary">June 11, 1985</Text>
          <Text type="secondary">robin2fly@example.com</Text>
        </Stack>
      </CardSection>
    </Card>
  )
}
