() => {
  const [collapsed, setCollapsed] = React.useState(false)
  return (
    <Stack>
      <Button onClick={() => setCollapsed(!collapsed)}>
        {collapsed ? "Close" : "Open"} collapse
      </Button>
      <Collapse
        expanded={collapsed}
        label="Duration"
        onClick={() => setCollapsed(!collapsed)}
      >
        <Slider
          label="Travel time"
          defaultValue={[0, 5]}
          maxValue={24}
          minValue={0}
        />
      </Collapse>
    </Stack>
  )
}
