() => {
  const [tags, setTags] = React.useState(["London", "Manchester", "Liverpool"])

  const removeTag = (tag) => {
    setTags(tags.filter((place) => place !== tag))
  }

  return (
    <InputField
      label="Destination"
      tags={
        <>
          {tags.map((tag) => (
            <Tag
              key={tag}
              onRemove={() => {
                removeTag(tag)
              }}
            >
              {tag}
            </Tag>
          ))}
        </>
      }
    />
  )
}
