import Heading from "@kiwicom/orbit-components/lib/Heading";
import List from "@kiwicom/orbit-components/lib/List";
import ListItem from "@kiwicom/orbit-components/lib/ListItem";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import InputStepper from "@kiwicom/orbit-components/lib/InputStepper";
import Text from "@kiwicom/orbit-components/lib/Text";
import TextLink from "@kiwicom/orbit-components/lib/TextLink";

example:
() => {
  const [actions, setActions] = React.useState([])
  const [currentValue, setCurrentValue] = React.useState(2)

  const addAction = (action) => {
    setActions([...actions, action])
  }

  return (
    <Stack direction="column">
      <Stack spacing="compact">
        <Stack direction="column">
          <Heading type="title3" as="h3">
            onChange
          </Heading>
          <Text>For getting the current value.</Text>
        </Stack>
        <div
          style={{
            maxWidth: "11em",
          }}
        >
          <InputStepper
            onChange={(value) => setCurrentValue(value)}
            label="Travelers"
            defaultValue={2}
            maxValue={10}
            minValue={1}
            titleIncrement="Add a traveler"
            titleDecrement="Remove a traveler"
          />
        </div>
        <Text>The current value is: {currentValue}</Text>
      </Stack>
      <Stack direction="column">
        <Heading type="title3" as="h3">
          All callbacks
        </Heading>
        <div
          style={{
            maxWidth: "11em",
          }}
        >
          <InputStepper
            onBlur={() => addAction("Blurred")}
            onChange={() => addAction("Changed")}
            onFocus={() => addAction("Focused")}
            label="Travelers"
            defaultValue={2}
            maxValue={10}
            minValue={1}
            titleIncrement="Add a traveler"
            titleDecrement="Remove a traveler"
          />
        </div>
        <Text>
          What has happened?{" "}
          <TextLink type="secondary" onClick={() => setActions([])}>
            Clear list
          </TextLink>
        </Text>
        {actions && (
          <List>
            {actions.map((action, i) => {
              // eslint-disable-next-line react/no-array-index-key
              return <ListItem key={i}>{action}</ListItem>
            })}
          </List>
        )}
      </Stack>
    </Stack>
  )
}
