import Button from "@kiwicom/orbit-components/lib/Button";
import Illustration from "@kiwicom/orbit-components/lib/Illustration";
import Modal from "@kiwicom/orbit-components/lib/Modal";
import ModalFooter from "@kiwicom/orbit-components/lib/ModalFooter";
import ModalHeader from "@kiwicom/orbit-components/lib/ModalHeader";

example:
() => {
  const [showModal, setShowModal] = React.useState(true)
  return (
    <>
      {showModal && (
        <Modal
          onClose={() => {
            setShowModal(false)
          }}
        >
          <ModalHeader
            title="Priority boarding"
            description="Skip the line and board in peace. Only $25!"
            illustration={<Illustration name="PriorityBoarding" />}
          />
          <ModalFooter>
            <Button>Add priority boarding</Button>
          </ModalFooter>
        </Modal>
      )}
      {!showModal && (
        <Button
          onClick={() => {
            setShowModal(true)
          }}
        >
          Show modal
        </Button>
      )}
    </>
  )
}
