imports:
import Button from "@kiwicom/orbit-components/lib/Button";
import Dialog from "@kiwicom/orbit-components/lib/Dialog";
import Illustration from "@kiwicom/orbit-components/lib/Illustration";

example:
() => {
  const [showDialog, setShowDialog] = React.useState(false)
  return (
    <>
      {showDialog && (
        <Dialog
          title="Add meal?"
          description="Enjoy a tasty meal on your journey."
          illustration={<Illustration name="Meal" alt="" />}
          primaryAction={
            <Button
              onClick={() => {
                setShowDialog(false)
              }}
            >
              Add for $5
            </Button>
          }
          secondaryAction={
            <Button
              type="secondary"
              onClick={() => {
                setShowDialog(false)
              }}
            >
              Cancel
            </Button>
          }
        />
      )}
      {!showDialog && (
        <Button
          onClick={() => {
            setShowDialog(true)
          }}
        >
          Show dialog
        </Button>
      )}
    </>
  )
}
