import defaultTheme from "@kiwicom/orbit-components/lib/defaultTheme";
import Grid from "@kiwicom/orbit-components/lib/Grid";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Text from "@kiwicom/orbit-components/lib/Text";

example:
(): React.Node => {
  const divStyle = {
    backgroundColor: defaultTheme.orbit.paletteCloudDark,
    border: `1px solid ${defaultTheme.orbit.paletteProductDark}`,
    boxSizing: "border-box",
    padding: defaultTheme.orbit.paddingTable,
  }
  return (
    <Stack>
      <Text>
        The spacing between the row&apos;s increases with increasing viewport
        size. The spacing that&apos;s applied is the one from the largest media
        query that returns true.
      </Text>
      <Grid
        rows="repeat(3, 60px)"
        mediumMobile={{
          rowGap: "4px",
        }}
        largeMobile={{
          rowGap: "12px",
        }}
        tablet={{
          rowGap: "20px",
        }}
        desktop={{
          rowGap: "28px",
        }}
        largeDesktop={{
          rowGap: "36px",
        }}
      >
        <div style={divStyle}>
          <Text>Row 1</Text>
        </div>
        <div style={divStyle}>
          <Text>Row 2</Text>
        </div>
        <div style={divStyle}>
          <Text>Row 3</Text>
        </div>
      </Grid>
    </Stack>
  )
}
