import InputStepperStateless from "@kiwicom/orbit-components/lib/InputStepperStateless";
import validateDecrement from "@kiwicom/orbit-components/lib/validateDecrement";
import validateIncrement from "@kiwicom/orbit-components/lib/validateIncrement";

example:
(): React.Element<"div"> => {
  const maxValue = 10
  const minValue = 1
  const step = 1
  const [value, setValue] = React.useState(3)

  const incrementValue = () => {
    setValue(
      validateIncrement({
        value,
        maxValue,
        step,
      })
    )
  }

  const decrementValue = () => {
    setValue(
      validateDecrement({
        value,
        minValue,
        step,
      })
    )
  }

  return (
    <div
      style={{
        maxWidth: "11em",
      }}
    >
      <InputStepperStateless
        value={`${value} adults`}
        disabledDecrement={value <= minValue}
        disabledIncrement={value >= maxValue}
        onIncrement={incrementValue}
        onDecrement={decrementValue}
        label="Travelers"
        titleIncrement="Add a traveler"
        titleDecrement="Remove a traveler"
      />
    </div>
  )
}
