() => {
  const [loadingNow, setLoadingNow] = React.useState(true)
  return (
    <Stack direction="column">
      <Loading
        loading={loadingNow}
        type="inlineLoader"
        text="Saving your request"
      >
        <Text>Your request has been saved!</Text>
      </Loading>
      <Loading
        loading={loadingNow}
        type="inlineLoader"
        text="Saving your request"
      />
      <Button onClick={() => setLoadingNow(!loadingNow)}>Toggle loading</Button>
    </Stack>
  )
}
