imports:
import Button from "@kiwicom/orbit-components/lib/Button";
import Modal from "@kiwicom/orbit-components/lib/Modal";
import ModalSection from "@kiwicom/orbit-components/lib/ModalSection";

example:
(): React.Node => {
  const [showModal, setShowModal] = React.useState(true)
  return (
    <>
      {showModal && (
        <Modal
          preventOverlayClose
          onClose={() => {
            setShowModal(false)
          }}
        >
          <ModalSection>
            This modal can only be closed by clicking the close button. Clicking
            outside the modal will not close it.
          </ModalSection>
        </Modal>
      )}
      {!showModal && (
        <Button
          onClick={() => {
            setShowModal(true)
          }}
        >
          Show modal
        </Button>
      )}
    </>
  )
}
