import Button from "@kiwicom/orbit-components/lib/Button";
import Heading from "@kiwicom/orbit-components/lib/Heading";
import Modal from "@kiwicom/orbit-components/lib/Modal";
import ModalFooter from "@kiwicom/orbit-components/lib/ModalFooter";
import ModalSection from "@kiwicom/orbit-components/lib/ModalSection";
import ModalHeader from "@kiwicom/orbit-components/lib/ModalHeader";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Text from "@kiwicom/orbit-components/lib/Text";

example:
() => {
  const [showModal, setShowModal] = React.useState(true)
  const [localScrollPosition, setLocalScrollPosition] = React.useState()
  const modalRef = React.useRef(null)

  const setScroll = () => {
    if (modalRef.current) {
      modalRef.current.setScrollPosition(100)
    }
  }

  const getScroll = () => {
    if (modalRef.current) {
      setLocalScrollPosition(modalRef.current.getScrollPosition())
    }
  }

  return (
    <>
      {showModal && (
        <Modal
          onClose={() => {
            setShowModal(false)
          }}
          ref={modalRef}
        >
          <ModalHeader
            title={
              <Stack direction="row" align="center" justify="between">
                <Heading>Scrollable modal</Heading>
                <Button type="secondary" onClick={getScroll}>
                  Scroll position: {localScrollPosition}
                </Button>
              </Stack>
            }
          />
          <ModalSection>
            <Text>
              Top of the modal. Scroll up here by clicking the button in the
              footer.
            </Text>
            <Text>
              To see the current scroll position at any time, use the button in
              the header.
            </Text>
          </ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalSection>Scroll down to button</ModalSection>
          <ModalFooter>
            <Button onClick={setScroll}>Change scrollTop</Button>
          </ModalFooter>
        </Modal>
      )}
      {!showModal && (
        <Button
          onClick={() => {
            setShowModal(true)
          }}
        >
          Show modal
        </Button>
      )}
    </>
  )
}
