import Button from "@kiwicom/orbit-components/lib/Button";
import ListChoice from "@kiwicom/orbit-components/lib/ListChoice";
import Popover from "@kiwicom/orbit-components/lib/Popover";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Accommodation from "@kiwicom/orbit-components/lib/icons/Accommodation";

example:
() => {
  const [isOpen, setIsOpen] = React.useState(false)
  const [is2star, setIs2star] = React.useState(true)
  const [is3star, setIs3star] = React.useState(true)
  const [is4star, setIs4star] = React.useState(true)
  return (
    <Popover
      actions={
        <Stack direction="row" justify="end">
          <Button type="primarySubtle" onClick={() => setIsOpen(false)}>
            Done
          </Button>
        </Stack>
      }
      opened={isOpen}
      content={
        <Stack spacing="small">
          <ListChoice
            title="2 star hotels"
            selectable
            selected={is2star}
            onClick={() => setIs2star(!is2star)}
            icon={<Icons.Accommodation />}
          />
          <ListChoice
            title="3 star hotels"
            selectable
            selected={is3star}
            onClick={() => setIs3star(!is3star)}
            icon={<Icons.Accommodation />}
          />
          <ListChoice
            title="4 star hotels"
            selectable
            selected={is4star}
            onClick={() => setIs4star(!is4star)}
            icon={<Icons.Accommodation />}
          />
        </Stack>
      }
    >
      <Button
        circled
        title="Hotel types"
        iconLeft={<Icons.Accommodation />}
        onClick={() => setIsOpen(true)}
      />
    </Popover>
  )
}
