import Modal from "@kiwicom/orbit-components/lib/Modal";
import ModalHeader from "@kiwicom/orbit-components/lib/ModalHeader";
import ModalSection from "@kiwicom/orbit-components/lib/ModalSection";
import Portal from "@kiwicom/orbit-components/lib/Portal";
import Text from "@kiwicom/orbit-components/lib/Text";
import TextLink from "@kiwicom/orbit-components/lib/TextLink";

example:
() => {
  const [showModal, setShowModal] = React.useState(false)
  return (
    <>
      <Portal>
        {showModal && (
          <Modal
            onClose={() => {
              setShowModal(false)
            }}
          >
            <ModalHeader title="More about portals" />
            <ModalSection>
              <Text>
                For more on portals and how they work, read the{" "}
                <TextLink href="https://reactjs.org/docs/portals.html" external>
                  React docs on portals.
                </TextLink>
              </Text>
            </ModalSection>
          </Modal>
        )}
      </Portal>
      {!showModal && (
        <Button
          onClick={() => {
            setShowModal(true)
          }}
        >
          Show modal
        </Button>
      )}
    </>
  )
}
