import Button from "@kiwicom/orbit-components/lib/Button";
import Drawer from "@kiwicom/orbit-components/lib/Drawer";
import LinkList from "@kiwicom/orbit-components/lib/LinkList";
import Portal from "@kiwicom/orbit-components/lib/Portal";
import TextLink from "@kiwicom/orbit-components/lib/TextLink";

example:
() => {
  const [showDrawer, setShowDrawer] = React.useState(false)
  return (
    <>
      <Portal renderInto="docs-root">
        {showDrawer && (
          <Drawer
            onClose={() => {
              setShowDrawer(false)
            }}
            shown={showDrawer}
          >
            <LinkList>
              <TextLink
                href="https://orbit.kiwi/components/drawer/"
                external
                type="secondary"
              >
                Drawer guidelines
              </TextLink>
              <TextLink
                href="https://orbit.kiwi/components/drawer/react/"
                external
                type="secondary"
              >
                Drawer React API
              </TextLink>
              <TextLink
                href="https://github.com/kiwicom/orbit/tree/master/packages/orbit-components/src/Drawer"
                external
                type="secondary"
              >
                Drawer on GitHub
              </TextLink>
            </LinkList>
          </Drawer>
        )}
      </Portal>
      {!showDrawer && (
        <Button
          onClick={() => {
            setShowDrawer(true)
          }}
        >
          Show drawer
        </Button>
      )}
    </>
  )
}
