import Select from "@kiwicom/orbit-components/lib/Select";
import List from "@kiwicom/orbit-components/lib/List";
import ListItem from "@kiwicom/orbit-components/lib/ListItem";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Text from "@kiwicom/orbit-components/lib/Text";
import TextLink from "@kiwicom/orbit-components/lib/TextLink";

example:
() => {
  const [actions, setActions] = React.useState([])
  const [country, setCountry] = React.useState("")

  const addAction = (action) => {
    setActions([...actions, action])
  }

  return (
    <Stack direction="column">
      <Select
        value={country}
        placeholder="Choose your country"
        options={[
          {
            label: "Algeria",
            value: "dz",
          },
          {
            label: "Bolivia",
            value: "bo",
          },
          {
            label: "Croatia",
            value: "hr",
          },
          {
            label: "Indonesia",
            value: "id",
          },
          {
            label: "Mexico",
            value: "mx",
          },
        ]}
        label="Nationality"
        onChange={(event) => {
          setCountry(event.target.value)
          addAction("Changed value")
        }}
        onBlur={() => addAction("Blurred")}
        onFocus={() => addAction("Focused")}
      />
      <Text>
        What has happened?{" "}
        <TextLink type="secondary" onClick={() => setActions([])}>
          Clear list
        </TextLink>
      </Text>
      {actions && (
        <List>
          {actions.map((action, i) => {
            // eslint-disable-next-line react/no-array-index-key
            return <ListItem key={i}>{action}</ListItem>
          })}
        </List>
      )}
    </Stack>
  )
}
