import SPACINGS from "@kiwicom/orbit-components/lib/SPACINGS/SPACINGS";
import defaultTheme from "@kiwicom/orbit-components/lib/defaultTheme";
import Stack from "@kiwicom/orbit-components/lib/Stack";
import Text from "@kiwicom/orbit-components/lib/Text";

example:
(): React.Node => {
  const divStyle = {
    width: "40px",
    height: "40px",
    backgroundColor: defaultTheme.orbit.paletteInkLight,
  }
  return (
    <Stack>
      <Text>
        The spacing between the stack&apos;s children increases with increasing
        viewport size. The spacing that&apos;s applied is the one from the
        largest media query that returns true.
      </Text>
      <Stack
        flex
        spacing={SPACINGS.XXSMALL}
        mediumMobile={{
          spacing: SPACINGS.XSMALL,
        }}
        largeMobile={{
          spacing: SPACINGS.SMALL,
        }}
        tablet={{
          spacing: SPACINGS.MEDIUM,
        }}
        desktop={{
          spacing: SPACINGS.LARGE,
        }}
        largeDesktop={{
          spacing: SPACINGS.XLARGE,
        }}
      >
        <div style={divStyle} />
        <div style={divStyle} />
        <div style={divStyle} />
      </Stack>
    </Stack>
  )
}
