import Stack from "@kiwicom/orbit-components/lib/Stack";
import StepperStateless from "@kiwicom/orbit-components/lib/StepperStateless";
import Heading from "@kiwicom/orbit-components/lib/Heading";
import validateDecrement from "@kiwicom/orbit-components/lib/validateDecrement";
import validateIncrement from "@kiwicom/orbit-components/lib/validateIncrement";

example:
() => {
  const maxValue = 10
  const minValue = 1
  const step = 1
  const [value, setValue] = React.useState(3)

  const incrementValue = () => {
    setValue(
      validateIncrement({
        value,
        maxValue,
        step,
      })
    )
  }

  const decrementValue = () => {
    setValue(
      validateDecrement({
        value,
        minValue,
        step,
      })
    )
  }

  return (
    <Stack flex align="center">
      <Heading type="title4">Travelers</Heading>
      <div
        style={{
          maxWidth: "10em",
        }}
      >
        <StepperStateless
          value={`${value} adults`}
          disabledDecrement={value <= minValue}
          disabledIncrement={value >= maxValue}
          onIncrement={incrementValue}
          onDecrement={decrementValue}
          titleIncrement="Add a traveler"
          titleDecrement="Remove a traveler"
        />
      </div>
    </Stack>
  )
}
